INSERT INTO object_localized_query_23_en_GB (`ooo_id`,`language`,`icon`,`name`,`seoname`,`description`,`link`,`images`,`documents`,`videos`,`downloads`) VALUES ('6342','en_GB','2043','Polygiene','Polygiene','<p>\n	<span style=\"text-align: justify; \">Sweat itself is odourless but tends to create the perfect environment for odour-causing bacteria to multiply. When sweat mixes with odour-causing bacteria on fabrics, odour results.&nbsp;</span><strong style=\"text-align: justify; \">Polygiene&reg;</strong><span style=\"text-align: justify; \">&nbsp;is a highly effective antimicrobial technology using natural silver salt in low concentrations to safely inhibit the growth of odour-causing bacteria and fungi. Polygiene&reg; does not affect the natural bacterial flora of your skin. Without odour-causing bacteria, you stay fresh.</span></p>\n<p style=\"text-align: justify; \">\n	<strong>Polygiene&reg;&nbsp;</strong>meets the highest standards of skin safety and hygiene. It does not interfere with the skin&rsquo;s natural bacterial flora and is approved for use on open wound bandages. Polygiene&reg; is bluesign&reg; approved, meeting the most demanding environmental certification for textiles, and is on the Oeko-Tex&reg; list of approved products.</p>\n<p style=\"text-align: justify; \">\n	&nbsp;</p>\n<p style=\"text-align: justify; \">\n	<a href=\"http://www.polygiene.com\">www.polygiene.com</a></p>\n','N;',NULL,NULL,NULL,NULL);
