INSERT INTO object_localized_query_23_en_GB (`ooo_id`,`language`,`icon`,`name`,`seoname`,`description`,`link`,`images`,`documents`,`videos`,`downloads`) VALUES ('6464','en_GB','1937','Women','Women','<p>\n	<span style=\"text-align: justify; \">Women are typically more sensitive to cold temperatures than men</span></p>\n<ul>\n	<li style=\"text-align: justify; \">\n		Their skin is thinner than men&rsquo;s skin and thus reacts quicker to cold&nbsp;</li>\n	<li style=\"text-align: justify; \">\n		They have less heat-producing muscle mass</li>\n	<li style=\"text-align: justify; \">\n		Their abdomen protects itself from cold and draws warmth from the extremities</li>\n</ul>\n<p style=\"text-align: justify; \">\n	&nbsp;</p>\n<p style=\"text-align: justify; \">\n	Our&nbsp;<strong>women&rsquo;s sleeping bags&nbsp;</strong>are designed to satisfy the specific female needs</p>\n<ul>\n	<li style=\"text-align: justify; \">\n		Additional thermal layer around the upper body</li>\n	<li style=\"text-align: justify; \">\n		Additional insulation layer in the area around the feet</li>\n	<li style=\"text-align: justify; \">\n		Specific shape for the female anatomy: Narrower around the shoulders, wider around the hips, extra width in the area of the elbows creating additional space for movement</li>\n</ul>\n','N;',NULL,NULL,NULL,NULL);
INSERT INTO object_localized_query_23_en_GB (`ooo_id`,`language`,`icon`,`name`,`seoname`,`description`,`link`,`images`,`documents`,`videos`,`downloads`) VALUES ('6469','en_GB','1939','X-Shield Construction','Sleepingbag X-Shield Construction','<p>\n	<span style=\"text-align: justify; \">Separate vertical and horizontal layers around the upper torso create additional loft and thus provide more warmth.&nbsp;</span><span style=\"text-align: justify; \">No additional weight.&nbsp;</span><span style=\"text-align: justify; \">Offered in Denali sleeping bags.</span></p>\n','N;',NULL,NULL,NULL,NULL);
INSERT INTO object_localized_query_23_en_GB (`ooo_id`,`language`,`icon`,`name`,`seoname`,`description`,`link`,`images`,`documents`,`videos`,`downloads`) VALUES ('6472','en_GB','1926','Shoulder / Knee Extension','Sleepingbag Shoulder / Knee Extension','<p>\n	<span style=\"text-align: justify; \">Agitated sleepers change their position up to 60 times every night. In a tight mummy sleeping bag, this causes undesired awakenings and rough nights.</span></p>\n<p>\n	<br style=\"text-align: justify; \" />\n	<span style=\"text-align: justify; \">By using slightly more material in the shoulder and knee areas, the sleeping person is less constricted and sleeps peacefully.&nbsp;</span><span style=\"text-align: justify; \">Considerably more space for movement.&nbsp;</span><span style=\"text-align: justify; \">Offered with all Denali sleeping bags.</span></p>\n','N;',NULL,NULL,NULL,NULL);
