INSERT INTO object_localized_query_23_en_GB (`ooo_id`,`language`,`icon`,`name`,`seoname`,`description`,`link`,`images`,`documents`,`videos`,`downloads`) VALUES ('6641','en_GB','1957','ErgoVent Vario','ErgoVent Vario','<p>\n	ErgoVent base construction with adjustable torso length.&nbsp;Back mesh allows for excellent 3-dimensional ventilation. A permanently elastic spring steel frame tautens the skin-friendly back mesh and ensures three-directional ventilation.</p>\n<div>\n	&nbsp;</div>\n<div>\n	The Vario version allows the release of the back panel`s preload by discharging the straps. It transforms the backpack into a body-contact model with the advantage of a form-fitting fit for difficult situations when it is important to shift the load closer and tighter to the body.&nbsp;</div>\n<div>\n	&nbsp;</div>\n<div>\n	Padded hip belt and anatomically shaped shoulder straps with 3D-mesh ensure secure fit and breathability.</div>\n<div>\n	&nbsp;</div>\n<div>\n	The ErgoVent Vario carrying system is used in day hiking backpacks and easy hut-to-hut tours.</div>\n<div>\n	&nbsp;</div>\n','N;',NULL,NULL,NULL,NULL);
