INSERT INTO object_localized_query_23_en_GB (`ooo_id`,`language`,`icon`,`name`,`seoname`,`description`,`link`,`images`,`documents`,`videos`,`downloads`) VALUES ('6739','en_GB','2007','OrthoLite®','OrthoLite®','<p>\n	OrthoLite&reg; stands for insoles made of a special PU open cell foam that offers extra comfort from the very first moment they are worn. Besides being very durable and extremely lightweight, the unique open cell formula of the OrthoLite&reg; foam ensures that all the major needs of the consumer are met:&nbsp;</p>\n<div>\n	&nbsp;</div>\n<ul>\n	<li>\n		Long-lasting cushioning: The superior cushioning allows the foam to retain 95% of its shape, providing consistent comfort and fit.</li>\n	<li>\n		High breathability: The open cell structure of OrthoLite&reg; allows air to circulate, keeping the foot noticeably cooler inside the shoe.</li>\n	<li>\n		Moisture transport: The structure of OrthoLite&reg; creates a moisture management system that moves moisture away from the foot, leaving it considerably drier.&nbsp;</li>\n	<li>\n		Anti-microbial agents: A patented biocide is present in the OrthoLite&reg; foam to fight against the presence of fungus, bacteria and odour.</li>\n	<li>\n		OrthoLite&reg; is washable, you can actually wash the insoles in your washing machine!</li>\n</ul>\n','N;',NULL,NULL,NULL,NULL);
INSERT INTO object_localized_query_23_en_GB (`ooo_id`,`language`,`icon`,`name`,`seoname`,`description`,`link`,`images`,`documents`,`videos`,`downloads`) VALUES ('6782','en_GB','1991','Contagrip™','Contagrip™','<p>\n	This high quality outsole with its specific rubber formulation is extremly robust and abrasion-resistant. It provides best grip and durability on all mountain surfaces.</p>\n<div>\n	&nbsp;</div>\n<div>\n	<em>&quot;Contagrip is much more than a singular technology. It is a range of complex outsole recipes, specifically designed to deliver the best combination of traction and durability for each intended use&quot;</em>, Michel Gauthier, developer of Contagrip</div>\n','N;',NULL,NULL,NULL,NULL);
